  /* @jsx mdx */
import CodeExplanation from '@site/src/components/CodeExplanation';
import ConfigExample from '@site/src/components/ConfigExample';
import DaemonDiagram from '@site/src/components/DaemonDiagram';
import InstallationCommand from '@site/src/components/InstallationCommand';
import Steps from '@site/src/components/Steps';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [{
  value: 'Install',
  id: 'install',
  children: [{
    value: 'Install Vector',
    id: 'install-vector-1',
    children: []
  }, {
    value: 'Configure Vector',
    id: 'configure-vector-1',
    children: []
  }, {
    value: 'Start Vector',
    id: 'start-vector-1',
    children: []
  }, {
    value: 'Configure Vector',
    id: 'configure-vector-2',
    children: []
  }, {
    value: 'Start the Vector container',
    id: 'start-the-vector-container',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This document will cover installing Vector on NixOS.`}</p>
    {
      /*
          THIS FILE IS AUTOGENERATED!
           To make changes please edit the template located at:
           website/docs/setup/installation/operating-systems/nixos.md.erb
      */
    }
    <h2 {...{
      "id": "install"
    }}>{`Install`}</h2>
    <Tabs block={true} defaultValue="daemon" values={[{
      "label": "As a Daemon",
      "value": "daemon"
    }]} mdxType="Tabs">
      <TabItem value="daemon" mdxType="TabItem">
        <p>{`The `}<a parentName="p" {...{
            "href": "/docs/setup/deployment/strategies/#daemon"
          }}>{`daemon deployment strategy`}</a>{` is designed for data
collection on a single host. Vector runs in the background, in its own process,
collecting `}<em parentName="p">{`all`}</em>{` data for that host.
Typically data is collected from a process manager, such as Journald via
Vector's `}<a parentName="p" {...{
            "href": "/docs/reference/sources/journald/"
          }}><inlineCode parentName="a">{`journald`}</inlineCode>{` source`}</a>{`, but can be collected
through any of Vector's `}<a parentName="p" {...{
            "href": "/docs/reference/sources/"
          }}>{`sources`}</a>{`.
The following diagram demonstrates how it works.`}</p>
        <DaemonDiagram platformName={null} sourceName={null} sinkName={null} mdxType="DaemonDiagram" />
        <hr></hr>
        <Tabs centered={true} className={"rounded"} defaultValue={"nix"} placeholder="Please choose an installation method..." select={false} size={null} values={[{
          "group": "Package managers",
          "label": "Nix",
          "value": "nix"
        }, {
          "group": "Nones",
          "label": "Vector CLI",
          "value": "vector-cli"
        }, {
          "group": "Platforms",
          "label": "Docker CLI",
          "value": "docker-cli"
        }, {
          "group": "Platforms",
          "label": "Docker Compose",
          "value": "docker-compose"
        }]} mdxType="Tabs">
          <TabItem value="nix" mdxType="TabItem">
            <Steps headingDepth={3} mdxType="Steps">
              <ol>
                <li parentName="ol">
                  <h3 parentName="li" {...{
                    "id": "install-vector"
                  }}>{`Install Vector`}</h3>
                  <pre parentName="li"><code parentName="pre" {...{
                      "className": "language-bash"
                    }}>{`nix-env --file https://github.com/NixOS/nixpkgs/archive/master.tar.gz --install --attr vector
`}</code></pre>
                  <CodeExplanation mdxType="CodeExplanation">
                    <ul parentName="li">
                      <li parentName="ul">{`The `}<inlineCode parentName="li">{`--file`}</inlineCode>{` flag ensures that you're installing the latest stable version
of Vector (0.9.2).`}</li>
                      <li parentName="ul">{`The `}<inlineCode parentName="li">{`--attr`}</inlineCode>{` improves installation speed.`}</li>
                    </ul>
                  </CodeExplanation>
                  <p parentName="li"><a parentName="p" {...{
                      "href": "/docs/setup/installation/package-managers/nix/#versions"
                    }}>{`Looking for a specific version?`}</a></p>
                </li>
                <li parentName="ol">
                  <h3 parentName="li" {...{
                    "id": "configure-vector"
                  }}>{`Configure Vector`}</h3>
                  <ConfigExample format="toml" path={"/etc/vector/vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                </li>
                <li parentName="ol">
                  <h3 parentName="li" {...{
                    "id": "start-vector"
                  }}>{`Start Vector`}</h3>
                  <pre parentName="li"><code parentName="pre" {...{
                      "className": "language-bash"
                    }}>{`vector --config /etc/vector/vector.toml
`}</code></pre>
                  <CodeExplanation mdxType="CodeExplanation">
                    <ul parentName="li">
                      <li parentName="ul"><inlineCode parentName="li">{`vector`}</inlineCode>{` is placed in your `}<inlineCode parentName="li">{`$PATH`}</inlineCode>{`.`}</li>
                      <li parentName="ul">{`You must create a `}<a parentName="li" {...{
                          "href": "/docs/setup/configuration/"
                        }}>{`Vector configuration file`}</a>{` to
successfully start Vector.`}</li>
                    </ul>
                  </CodeExplanation>
                </li>
              </ol>
            </Steps>
          </TabItem>
          <TabItem value="vector-cli" mdxType="TabItem">
            <Steps headingDepth={3} mdxType="Steps">
              <ol>
                <li>
                  <h3 {...{
                    "id": "install-vector-1"
                  }}>{`Install Vector`}</h3>
                  <InstallationCommand mdxType="InstallationCommand" />
                  <p>{`Or choose your `}<a parentName="p" {...{
                      "href": "/docs/setup/installation/"
                    }}>{`preferred method`}</a>{`.`}</p>
                </li>
                <li>
                  <h3 {...{
                    "id": "configure-vector-1"
                  }}>{`Configure Vector`}</h3>
                  <ConfigExample format="toml" path={"vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                </li>
                <li>
                  <h3 {...{
                    "id": "start-vector-1"
                  }}>{`Start Vector`}</h3>
                  <pre><code parentName="pre" {...{
                      "className": "language-bash"
                    }}>{`vector --config vector.toml
`}</code></pre>
                  <p>{`That's it! Simple and to the point. Hit `}<inlineCode parentName="p">{`ctrl+c`}</inlineCode>{` to exit.`}</p>
                </li>
              </ol>
            </Steps>
          </TabItem>
          <TabItem value="docker-cli" mdxType="TabItem">
            <Steps headingDepth={3} mdxType="Steps">
              <ol>
                <li>
                  <h3 {...{
                    "id": "configure-vector-2"
                  }}>{`Configure Vector`}</h3>
                  <ConfigExample format="toml" path={"/etc/vector/vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                </li>
                <li>
                  <h3 {...{
                    "id": "start-the-vector-container"
                  }}>{`Start the Vector container`}</h3>
                  <pre><code parentName="pre" {...{
                      "className": "language-bash"
                    }}>{`docker run \\
  -v $PWD/vector.toml:/etc/vector/vector.toml:ro \\
  timberio/vector:latest-alpine
`}</code></pre>
                  <CodeExplanation mdxType="CodeExplanation">
                    <ul>
                      <li parentName="ul">{`The `}<inlineCode parentName="li">{`-v $PWD/vector.to...`}</inlineCode>{` flag passes your custom configuration to Vector.`}</li>
                      <li parentName="ul">{`The `}<inlineCode parentName="li">{`timberio/vector:latest-alpine`}</inlineCode>{` is the default image we've chosen, you are welcome to use `}<a parentName="li" {...{
                          "href": "/docs/setup/installation/platforms/docker/#variants"
                        }}>{`other image variants`}</a>{`.`}</li>
                    </ul>
                  </CodeExplanation>
                  <p>{`That's it! Simple and to the point. Hit `}<inlineCode parentName="p">{`ctrl+c`}</inlineCode>{` to exit.`}</p>
                </li>
              </ol>
            </Steps>
          </TabItem>
          <TabItem value="docker-compose" mdxType="TabItem">
            <p>{`compose!`}</p>
          </TabItem>
        </Tabs>
      </TabItem>
    </Tabs>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
