export const rightToc = [{
  value: 'Upgrade Guide',
  id: 'upgrade-guide',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`We are currently working to improve and optimize our `}<a parentName="p" {...{
        "href": "/docs/about/data-model/log/"
      }}><inlineCode parentName="a">{`log`}</inlineCode>{` data
model`}</a>{`. Initial versions of this data model were
represented as a flat map for key access optimizations. This proved over time
to not be as helpful as we had hoped. As a result we are working to move our
data model to be as close to JSON as possible. This means:`}</p>
    <ol>
      <li parentName="ol"><inlineCode parentName="li">{`null`}</inlineCode>{` values are now supported in Vector's data model.`}</li>
      <li parentName="ol">{`Nested fields are represented in an actual nested representation.`}</li>
    </ol>
    <p>{`Both of these changes bring Vector's internal data model closer to JSON.
Unfortunately, this breaks disk buffer serialization which means you must
drain your disk bufffer before upgrading Vector.`}</p>
    <h2 {...{
      "id": "upgrade-guide"
    }}>{`Upgrade Guide`}</h2>
    <ol>
      <li parentName="ol">{`Make sure Vector shuts down normally to ensure your disk buffers are fully
drained.`}</li>
      <li parentName="ol">{`That's it! Update Vector as usual.`}</li>
    </ol>
    <p>{`Note, Vector will discard invalid disk buffer data, bad data will not prevent
Vector from starting.`}</p>
    </MDXLayout>;
}

;
