      <Field common={true} defaultValue={null} enumValues={null} examples={["my.host.com"]} groups={[]} name={"host"} path={null} relevantWhen={null} required={true} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "host"
        }}>{`host`}</h3>
        <p>{`The current hostname, equivalent to the `}<inlineCode parentName="p">{`gethostname`}</inlineCode>{` command. This can be
renamed via the `}<a parentName="p" {...{
            "href": "#host_key"
          }}><inlineCode parentName="a">{`host_key`}</inlineCode></a>{` option.`}</p>
      </Field>
