  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: '创世块配置',
	description: 'The fundamental Muta concepts. A great place to start learning about Muta.'
};
export const metadata = {
  "id": "docs_zh/setup/genesis-config",
  "title": "创世块配置",
  "description": "The fundamental Muta concepts. A great place to start learning about Muta.",
  "source": "@site/docs/docs_zh/setup/genesis-config.md",
  "permalink": "/new-docs/docs/docs_zh/setup/genesis-config",
  "editUrl": "https://github.com/nervosnetwork/muta/edit/master/website/docs/docs_zh/setup/genesis-config.md",
  "sidebar": "docs_zh",
  "previous": {
    "title": "配置介绍",
    "permalink": "/new-docs/docs/docs_zh/setup/config"
  },
  "next": {
    "title": "节点运行配置",
    "permalink": "/new-docs/docs/docs_zh/setup/node-config"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '概览',
  id: '概览',
  children: []
}, {
  value: '参数解释',
  id: '参数解释',
  children: [{
    value: '初始化参数',
    id: '初始化参数',
    children: []
  }, {
    value: 'Asset service 参数',
    id: 'asset-service-参数',
    children: []
  }, {
    value: 'Metadata service 参数',
    id: 'metadata-service-参数',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`本文档将详细介绍如何配置创世块，配置文件为 `}<inlineCode parentName="p">{`devtools/chain/genesis.toml`}</inlineCode>{` 。`}</p>
    <h2 {...{
      "id": "概览"
    }}>{`概览`}</h2>
    <p>{`一条链的不同节点的创世块文件必须是相同的。链一启动，创世块的许多配置就不能改变了，在下面的参数解释里，我们会将可以改变的参数标注为”可升级“。`}</p>
    <p>{`让我们先来看下 `}<inlineCode parentName="p">{`devtools/chain/genesis.toml`}</inlineCode>{`：`}</p>
    <pre><code parentName="pre" {...{}}>{`timestamp = 0
prevhash = "0x44915be5b6c20b0678cf05fcddbbaa832e25d7e6ac538784cd5c24de00d47472"

[[services]]
name = "asset"
payload = '''
{
    "id": "0xf56924db538e77bb5951eb5ff0d02b88983c49c45eea30e8ae3e7234b311436c",
    "name": "MutaToken",
    "symbol": "MT",
    "supply": 320000011,
    "issuer": "0xf8389d774afdad8755ef8e629e5a154fddc6325a"
}
'''

[[services]]
name = "metadata"
payload = '''
{
    "chain_id": "0xb6a4d7da21443f5e816e8700eea87610e6d769657d6b8ec73028457bf2ca4036",
    "common_ref": "0x703873635a6b51513451",
    "timeout_gap": 20,
    "cycles_limit": 4294967295,
    "cycles_price": 1,
    "interval": 3000,
    "verifier_list": [
        {
            "bls_pub_key": "0x04188ef9488c19458a963cc57b567adde7db8f8b6bec392d5cb7b67b0abc1ed6cd966edc451f6ac2ef38079460eb965e890d1f576e4039a20467820237cda753f07a8b8febae1ec052190973a1bcf00690ea8fc0168b3fbbccd1c4e402eda5ef22",
            "address": "0xf8389d774afdad8755ef8e629e5a154fddc6325a",
            "propose_weight": 1,
            "vote_weight": 1
        }
    ],
    "propose_ratio": 15,
    "prevote_ratio": 10,
    "precommit_ratio": 10,
    "brake_ratio": 7,
    "tx_num_limit": 20000,
    "max_tx_size": 1024
}
'''
`}</code></pre>
    <h2 {...{
      "id": "参数解释"
    }}>{`参数解释`}</h2>
    <h3 {...{
      "id": "初始化参数"
    }}>{`初始化参数`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数名称`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数解释`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`默认值`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`timestamp`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`创世块的时间戳，可以随意设置，配置成 0，或者当天 0 点的时间都可以。`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`prevhash`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`可以随意设置，只会影响查询创世块时的字段显示。`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0x`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "asset-service-参数"
    }}>{`Asset service 参数`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数名称`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数解释`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`默认值`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`id`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`资产的唯一 id，建议设置成 hash ，以免在之后和链上其他资产重复`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`name`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`资产名字`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`0x`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`symbol`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`资产简称`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`supply`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`资产发行总量`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`issuer`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`发行方地址`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "metadata-service-参数"
    }}>{`Metadata service 参数`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数名称`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`参数解释`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}>{`默认值`}</th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`chain_id`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`链唯一 id，建议设置为任意 hash`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`common_ref`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`BLS 签名需要`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`timeout_gap`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`交易池能接受的最大超时块范围。用户在发送交易的时候，需要填写 `}<inlineCode parentName="td">{`timeout`}</inlineCode>{` 字段，表示块高度超过这个值后，如果该交易还没有被打包，则以后都不会被打包，这样可以确保之前的某笔交易超时后一定会失败，避免用户的交易很长时间未被打包后换 `}<inlineCode parentName="td">{`nonce`}</inlineCode>{` 重发交易，结果两笔交易都上链的情况。当用户填写的 `}<inlineCode parentName="td">{`timeout`}</inlineCode>{` > `}<inlineCode parentName="td">{`chain_current_height`}</inlineCode>{` + `}<inlineCode parentName="td">{`timeout_gap`}</inlineCode>{` 时，交易池会拒绝这笔交易。考虑到一些特殊情况（比如一些冷钱包对交易签名后较长时间才发出），该值可以适当调大`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`20`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`cycles_limit`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`10进制，链级别对单个交易可以消耗的最大 `}<inlineCode parentName="td">{`cycle`}</inlineCode>{` 的限制`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1000000`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`cycles_price`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`最小 cycle 价格，目前没有使用`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`interval`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`出块间隔，单位为 ms。当设置为 3s 的时候，出块间隔并不是严格的 3s，而是在 3s 附近波动，这是因为 Overlord 共识在响应性上的优化。当网络状况较好的时候，会小于 3s，网络情况较差，则会略大于 3s。`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`3000`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><strong parentName="td">{`verifier_list`}</strong></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`bls_pub_key`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`节点的 BLS 公钥`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`address`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`节点的地址`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`propose_weight`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`节点的出块权重。如果有四个共识节点，出块权重分别为 1, 2, 3, 4，则第一个节点的出块概率为 1 / (1 + 2 + 3 + 4)。投票权重的逻辑类似。`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`vote_weight`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`节点的投票权重`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`propose_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`propose 阶段的超时时间与出块时间的比例。例如 propose_ratio 为 5, interval 为 3000，则 propose 阶段的超时时间为 15 / 10 * 3000 = 4500，单位均为毫秒。`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`15`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`prevote_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`prevote 阶段的超时时间与出块时间的比例`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`10`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`precommit_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`precommit 阶段的超时时间与出块时间的比例`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`10`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`brake_ratio`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`brake 阶段的超时时间与出块时间的比例`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`7`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`tx_num_limit`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`每一个块里最多可以打包的交易数`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`20000`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`max_tx_size`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`单个交易最大的字节数`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`1024`}</td>
          <td parentName="tr" {...{
            "align": "left"
          }}></td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
