  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	last_modified_on: '2020-07-13',
	title: 'Validating',
	description: 'How to validation Vector\'s configuration to ensure it is error free before applying it.'
};
export const metadata = {
  "id": "administration/validating",
  "title": "Validating",
  "description": "How to validation Vector's configuration to ensure it is error free before applying it.",
  "source": "@site/docs/administration/validating.md",
  "permalink": "/docs/administration/validating",
  "editUrl": "https://github.com/timberio/vector/edit/master/website/docs/administration/validating.md",
  "sidebar": "docs",
  "previous": {
    "title": "Updating",
    "permalink": "/docs/administration/updating"
