  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	last_modified_on: '2020-07-13',
	$schema: '/.meta/.schemas/highlights.json',
	title: 'Sink options have been refactored',
	description: 'We\'ve simplified and organized our sink options',
	author_github: 'https://github.com/binarylogic',
	pr_numbers: [
		1006,
		1493,
		1494,
		1495
	],
	release: '0.7.0',
	hide_on_release_notes: false,
	tags: [
		'type: breaking change',
		'domain: buffers',
		'domain: config',
		'domain: sinks'
	]
};
export const metadata = {
  "date": "2020-01-12T00:00:00.000Z",
  "description": "We've simplified and organized our sink options",
  "permalink": "/highlights/2020-01-12-request-options-refactored",
  "readingTime": "2 min read",
  "source": "@site/highlights/2020-01-12-request-options-refactored.md",
  "tags": [
    {
      "label": "type: breaking change",
      "permalink": "/highlights/tags/type-breaking-change"
    },
    {
      "label": "domain: buffers",
      "permalink": "/highlights/tags/domain-buffers"
    },
    {
      "label": "domain: config",
      "permalink": "/highlights/tags/domain-config"
    },
    {
      "label": "domain: sinks",
      "permalink": "/highlights/tags/domain-sinks"
    }
  ],
  "title": "Sink options have been refactored",
  "truncated": false,
  "prevItem": {
    "title": "Rename `line` field to `message` in `splunk_hec` source",
    "permalink": "/highlights/2020-01-14-rename-line-field-to-message"
  },
  "nextItem": {
    "title": "Use comma delim server list in `kafka` sink",
    "permalink": "/highlights/2020-01-12-use-comma-delim-server-list-instead-of-an-array"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Upgrade Guide',
  id: 'upgrade-guide',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`In our preparation for 1.0 we took time to organize and cleanup our
request-based sink options. The specific changes include:`}</p>
    <ol>
      <li parentName="ol"><inlineCode parentName="li">{`request_*`}</inlineCode>{` options have been moved under a `}<inlineCode parentName="li">{`request`}</inlineCode>{` table.`}</li>
      <li parentName="ol"><inlineCode parentName="li">{`retry_backoff_secs`}</inlineCode>{` must also be replaced with two new fields
`}<inlineCode parentName="li">{`retry_initial_backoff_secs`}</inlineCode>{` and `}<inlineCode parentName="li">{`retry_max_duration_secs`}</inlineCode>{`.`}</li>
      <li parentName="ol"><inlineCode parentName="li">{`batch_*`}</inlineCode>{` options have been moved under a `}<inlineCode parentName="li">{`batch`}</inlineCode>{` table.`}</li>
      <li parentName="ol"><inlineCode parentName="li">{`batch_size`}</inlineCode>{` has been relaced with either `}<inlineCode parentName="li">{`batch.max_events`}</inlineCode>{` or
`}<inlineCode parentName="li">{`batch.max_size`}</inlineCode>{` in order to clarify its purpose (capping discrete events or
bytes respectively).`}</li>
      <li parentName="ol"><inlineCode parentName="li">{`basic_auth`}</inlineCode>{` fields have been moved to a general purpose `}<inlineCode parentName="li">{`auth`}</inlineCode>{` table
complemented with a `}<inlineCode parentName="li">{`strategy`}</inlineCode>{` field.`}</li>
    </ol>
    <p>{`These changes effect the following sinks:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`aws_cloudwatch_logs`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`aws_kinesis_firehose`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`aws_kinesis_streams`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`aws_s3`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`clickhouse`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`datadog_metrics`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`elasticsearch`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`gcp_pubsub`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`http`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`new_relic_logs`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`splunk_hec`}</inlineCode></li>
    </ul>
    <h2 {...{
      "id": "upgrade-guide"
    }}>{`Upgrade Guide`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-diff",
        "metastring": "title=\"vector.toml\"",
        "title": "\"vector.toml\""
      }}>{` [sinks.my_sink_id]
   type = "http"
   inputs = ["my-source-id"]
   uri = "https://10.22.212.22:9000/endpoint"

-  batch_size = 1049000
+  [sinks.my_sink_id.batch]
+    max_size = 1049000

-  [sinks.my_sink_id.basic_auth]
+  [sinks.my_sink_id.auth]
+    strategy = "basic"
     user = "\${USERNAME_ENV_VAR}"
     password = "\${PASSWORD_ENV_VAR}"

-  request_in_flight_limit = 5
-  request_retry_backoff_secs = 1
+  [sinks.my_sink_id.request]
+    in_flight_limit = 5
+    retry_initial_backoff_secs = 1
+    retry_max_duration_secs = 10
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
