  /* @jsx mdx */
import Jump from '@site/src/components/Jump';
import SVG from 'react-inlinesvg';
export const rightToc = [{
  value: 'Principles',
  id: 'principles',
  children: []
}, {
  value: 'Who should use Vector?',
  id: 'who-should-use-vector',
  children: []
}, {
  value: 'Community',
  id: 'community',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">



    <SVG src="/img/components.svg" mdxType="SVG" />
    <p>{`Vector is a lightweight, ultra-fast, `}<a parentName="p" {...{
        "href": "https://github.com/timberio/vector"
      }}>{`open-source`}</a>{` tool for
building observability pipelines. Compared to Logstash and friends, Vector
`}<a parentName="p" {...{
        "href": "https://vector.dev/#performance"
      }}>{`improves throughput by ~10X while significantly reducing CPU and memory
usage`}</a>{`.`}</p>
    <h3 {...{
      "id": "principles"
    }}>{`Principles`}</h3>
    <ul>
      <li parentName="ul"><strong parentName="li">{`Reliability First.`}</strong>{` - Built in `}<a parentName="li" {...{
          "href": "https://www.rust-lang.org/"
        }}>{`Rust`}</a>{`, Vector's primary design goal is reliability.`}</li>
      <li parentName="ul"><strong parentName="li">{`One Tool. All Data.`}</strong>{` - One simple tool gets your `}<a parentName="li" {...{
          "href": "/docs/about/data-model/log/"
        }}>{`logs`}</a>{`, `}<a parentName="li" {...{
          "href": "/docs/about/data-model/metric/"
        }}>{`metrics`}</a>{`, and traces (coming soon) from A to B.`}</li>
      <li parentName="ul"><strong parentName="li">{`Single Responsibility.`}</strong>{` - Vector is a `}<em parentName="li">{`data router`}</em>{`, it does not plan to become a distributed processing framework.`}</li>
    </ul>
    <h3 {...{
      "id": "who-should-use-vector"
    }}>{`Who should use Vector?`}</h3>
    <ul>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD`}</em>{` use Vector to replace Logstash, Fluent*, Telegraf, Beats, or similar tools.`}</li>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD`}</em>{` use Vector as a `}<a parentName="li" {...{
          "href": "/docs/setup/deployment/strategies/#daemon"
        }}>{`daemon`}</a>{` or `}<a parentName="li" {...{
          "href": "/docs/setup/deployment/strategies/#sidecar"
        }}>{`sidecar`}</a>{`.`}</li>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD`}</em>{` use Vector as a Kafka consumer/producer for observability data.`}</li>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD`}</em>{` use Vector in resource constrained environments (such as devices).`}</li>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD NOT`}</em>{` use Vector if you need an advanced distributed stream processing framework.`}</li>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD NOT`}</em>{` use Vector to replace Kafka. Vector is designed to work with Kafka!`}</li>
      <li parentName="ul">{`You `}<em parentName="li">{`SHOULD NOT`}</em>{` use Vector for non-observability data such as analytics data.`}</li>
    </ul>
    <h3 {...{
      "id": "community"
    }}>{`Community`}</h3>
    <ul>
      <li parentName="ul">{`Vector is `}<strong parentName="li">{`downloaded over 100,000 times per day`}</strong>{`.`}</li>
      <li parentName="ul">{`Vector's largest user `}<strong parentName="li">{`processes over 10TB daily`}</strong>{`.`}</li>
      <li parentName="ul">{`Vector is `}<strong parentName="li">{`used by multiple fortune 500 companies`}</strong>{` with stringent production requirements.`}</li>
      <li parentName="ul">{`Vector has `}<strong parentName="li">{`over 15 active contributors`}</strong>{` and growing.`}</li>
    </ul>
    <Jump to="/guides/getting-started/" mdxType="Jump">Get started</Jump>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
