import Alert from '@site/src/components/Alert';
import Tabs from '@theme/Tabs';
import TabItem from '@theme/TabItem';
export const rightToc = [{
  value: '1. Check for any known issues',
  id: '1-check-for-any-known-issues',
  children: []
}, {
  value: '2. Check Vector&#39;s logs',
  id: '2-check-vectors-logs',
  children: []
}, {
  value: '3. Enable backtraces',
  id: '3-enable-backtraces',
  children: []
}, {
