  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`The Vector `}<inlineCode parentName="p">{`file`}</inlineCode>{` sink
`}<a parentName="p" {...{
        "href": "#streaming"
      }}>{`streams`}</a>{` `}<a parentName="p" {...{
        "href": "/docs/about/data-model/log/"
      }}><inlineCode parentName="a">{`log`}</inlineCode></a>{` events to a file.`}</p>
    {
      /*
          THIS FILE IS AUTOGENERATED!
           To make changes please edit the template located at:
           website/docs/reference/sinks/file.md.erb
      */
    }
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <Tabs block={true} defaultValue="common" values={[{
      "label": "Common",
      "value": "common"
    }, {
      "label": "Advanced",
      "value": "advanced"
    }]} mdxType="Tabs">
      <TabItem value="common" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[sinks.my_sink_id]
  # General
  type = "file" # required
  inputs = ["my-source-or-transform-id"] # required
  healthcheck = true # optional, default
  path = "vector-%Y-%m-%d.log" # required

  # Encoding
  encoding.codec = "ndjson" # required
`}</code></pre>
      </TabItem>
      <TabItem value="advanced" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[sinks.my_sink_id]
  # General
  type = "file" # required
  inputs = ["my-source-or-transform-id"] # required
  healthcheck = true # optional, default
  idle_timeout_secs = "30" # optional, default
  path = "vector-%Y-%m-%d.log" # required

  # Encoding
  encoding.codec = "ndjson" # required
  encoding.except_fields = ["timestamp", "message", "host"] # optional, no default
  encoding.only_fields = ["timestamp", "message", "host"] # optional, no default
  encoding.timestamp_format = "rfc3339" # optional, default
`}</code></pre>
      </TabItem>
    </Tabs>
    <Fields filters={true} mdxType="Fields">
      <Field common={true} defaultValue={null} enumValues={null} examples={[]} groups={[]} name={"encoding"} path={null} relevantWhen={null} required={true} templateable={false} type={"table"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "encoding"
        }}>{`encoding`}</h3>
        <p>{`Configures the encoding specific sink behavior.`}</p>
        <Fields filters={false} mdxType="Fields">
          <Field common={true} defaultValue={null} enumValues={{
            "ndjson": "Each event is encoded into JSON and the payload is new line delimited.",
            "text": "Each event is encoded into text via the `message` key and the payload is new line delimited."
          }} examples={["ndjson", "text"]} groups={[]} name={"codec"} path={"encoding"} relevantWhen={null} required={true} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "codec"
            }}>{`codec`}</h4>
            <p>{`The encoding codec used to serialize the events before outputting.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[["timestamp", "message", "host"]]} groups={[]} name={"except_fields"} path={"encoding"} relevantWhen={null} required={false} templateable={false} type={"[string]"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "except_fields"
            }}>{`except_fields`}</h4>
            <p>{`Prevent the sink from encoding the specified labels.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[["timestamp", "message", "host"]]} groups={[]} name={"only_fields"} path={"encoding"} relevantWhen={null} required={false} templateable={false} type={"[string]"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "only_fields"
            }}>{`only_fields`}</h4>
            <p>{`Limit the sink to only encoding the specified labels.`}</p>
          </Field>
          <Field common={false} defaultValue={"rfc3339"} enumValues={{
            "rfc3339": "Format as an RFC3339 string",
            "unix": "Format as a unix timestamp, can be parsed as a Clickhouse DateTime"
          }} examples={["rfc3339", "unix"]} groups={[]} name={"timestamp_format"} path={"encoding"} relevantWhen={null} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "timestamp_format"
            }}>{`timestamp_format`}</h4>
            <p>{`How to format event timestamps.`}</p>
          </Field>
        </Fields>
      </Field>
      <Field common={true} defaultValue={true} enumValues={null} examples={[true, false]} groups={[]} name={"healthcheck"} path={null} relevantWhen={null} required={false} templateable={false} type={"bool"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "healthcheck"
        }}>{`healthcheck`}</h3>
        <p>{`Enables/disables the sink healthcheck upon start.`}</p>
      </Field>
      <Field common={false} defaultValue={"30"} enumValues={null} examples={["30"]} groups={[]} name={"idle_timeout_secs"} path={null} relevantWhen={null} required={false} templateable={false} type={"uint"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "idle_timeout_secs"
        }}>{`idle_timeout_secs`}</h3>
        <p>{`The amount of time a file can be idle  and stay open. After not receiving any
events for this timeout, the file will be flushed and closed.`}</p>
      </Field>
      <Field common={true} defaultValue={null} enumValues={null} examples={["vector-%Y-%m-%d.log", "application-{{ application_id }}-%Y-%m-%d.log"]} groups={[]} name={"path"} path={null} relevantWhen={null} required={true} templateable={true} type={"string"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "path"
        }}>{`path`}</h3>
        <p>{`File name to write events to.
See `}<a parentName="p" {...{
            "href": "#template-syntax"
          }}>{`Template Syntax`}</a>{` for more info.`}</p>
      </Field>
    </Fields>
    <h2 {...{
      "id": "how-it-works"
    }}>{`How It Works`}</h2>
    <h3 {...{
      "id": "dynamic-file-and-directory-creation"
    }}>{`Dynamic File And Directory Creation`}</h3>
    <p>{`Vector will attempt to create the entire directory structure and the file when
emitting events to the file sink. This requires that the Vector agent have
the correct permissions to create and write to files in the specified directories.`}</p>
    <h3 {...{
      "id": "environment-variables"
    }}>{`Environment Variables`}</h3>
    <p>{`Environment variables are supported through all of Vector's configuration.
Simply add `}<inlineCode parentName="p">{`\${MY_ENV_VAR}`}</inlineCode>{` in your Vector configuration file and the variable
will be replaced before being evaluated.`}</p>
    <p>{`You can learn more in the
`}<a parentName="p" {...{
        "href": "/docs/setup/configuration/#environment-variables"
      }}>{`Environment Variables`}</a>{` section.`}</p>
    <h3 {...{
      "id": "streaming"
    }}>{`Streaming`}</h3>
    <p>{`The `}<inlineCode parentName="p">{`file`}</inlineCode>{` sink streams data on a real-time
event-by-event basis. It does not batch data.`}</p>
    <h3 {...{
      "id": "template-syntax"
    }}>{`Template Syntax`}</h3>
    <p>{`The `}<a parentName="p" {...{
        "href": "#path"
      }}><inlineCode parentName="a">{`path`}</inlineCode></a>{` options
support `}<a parentName="p" {...{
        "href": "/docs/reference/templating/"
      }}>{`Vector's template syntax`}</a>{`, enabling dynamic
values derived from the event's data. This syntax accepts
`}<a parentName="p" {...{
        "href": "https://docs.rs/chrono/0.4.11/chrono/format/strftime/index.html#specifiers"
      }}>{`strptime specifiers`}</a>{` as well as the
`}<inlineCode parentName="p">{`{{ field_name }}`}</inlineCode>{` syntax for accessing event fields. For example:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-toml",
        "metastring": "title=\"vector.toml\"",
        "title": "\"vector.toml\""
      }}>{`[sinks.my_file_sink_id]
  # ...
  path = "vector-%Y-%m-%d.log"
  path = "application-{{ application_id }}-%Y-%m-%d.log"
  # ...
`}</code></pre>
    <p>{`You can learn more about the complete syntax in the
`}<a parentName="p" {...{
        "href": "/docs/reference/templating/"
      }}>{`templating reference`}</a>{`.`}</p>
    </MDXLayout>;
}

