const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`The Vector `}<inlineCode parentName="p">{`papertrail`}</inlineCode>{` sink
`}<a parentName="p" {...{
        "href": "#streaming"
      }}>{`streams`}</a>{` `}<a parentName="p" {...{
        "href": "/docs/about/data-model/log/"
      }}><inlineCode parentName="a">{`log`}</inlineCode></a>{` events to
`}<a parentName="p" {...{
        "href": "https://www.papertrail.com/"
      }}>{`Papertrail`}</a>{` via `}<a parentName="p" {...{
        "href": "https://help.papertrailapp.com/kb/how-it-works/http-api/#submitting-log-messages"
      }}>{`Syslog`}</a>{`.`}</p>
    {
      /*
          THIS FILE IS AUTOGENERATED!
           To make changes please edit the template located at:
           website/docs/reference/sinks/papertrail.md.erb
      */
    }
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <Tabs block={true} defaultValue="common" values={[{
      "label": "Common",
      "value": "common"
    }, {
      "label": "Advanced",
      "value": "advanced"
    }]} mdxType="Tabs">
      <TabItem value="common" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[sinks.my_sink_id]
  # General
  type = "papertrail" # required
  inputs = ["my-source-or-transform-id"] # required
  endpoint = "logs.papertrailapp.com:12345" # required
  healthcheck = true # optional, default

  # Encoding
  encoding.codec = "json" # required
`}</code></pre>
      </TabItem>
      <TabItem value="advanced" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[sinks.my_sink_id]
  # General
  type = "papertrail" # required
  inputs = ["my-source-or-transform-id"] # required
  endpoint = "logs.papertrailapp.com:12345" # required
  healthcheck = true # optional, default

  # Buffer
  buffer.max_events = 500 # optional, default, events, relevant when type = "memory"
  buffer.max_size = 104900000 # required, bytes, required when type = "disk"
  buffer.type = "memory" # optional, default
  buffer.when_full = "block" # optional, default

  # Encoding
  encoding.codec = "json" # required
  encoding.except_fields = ["timestamp", "message", "host"] # optional, no default
  encoding.only_fields = ["timestamp", "message", "host"] # optional, no default
  encoding.timestamp_format = "rfc3339" # optional, default
`}</code></pre>
      </TabItem>
    </Tabs>
    <Fields filters={true} mdxType="Fields">
      <Field common={false} defaultValue={null} enumValues={null} examples={[]} groups={[]} name={"buffer"} path={null} relevantWhen={null} required={false} templateable={false} type={"table"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "buffer"
        }}>{`buffer`}</h3>
        <p>{`Configures the sink specific buffer behavior.`}</p>
        <Fields filters={false} mdxType="Fields">
          <Field common={true} defaultValue={500} enumValues={null} examples={[500]} groups={[]} name={"max_events"} path={"buffer"} relevantWhen={{
            "type": "memory"
          }} required={false} templateable={false} type={"uint"} unit={"events"} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "max_events"
            }}>{`max_events`}</h4>
            <p>{`The maximum number of `}<a parentName="p" {...{
                "href": "/docs/about/data-model/"
              }}>{`events`}</a>{` allowed in the buffer.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[104900000]} groups={[]} name={"max_size"} path={"buffer"} relevantWhen={{
            "type": "disk"
          }} required={true} templateable={false} type={"uint"} unit={"bytes"} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "max_size"
            }}>{`max_size`}</h4>
            <p>{`The maximum size of the buffer on the disk.`}</p>
          </Field>
          <Field common={true} defaultValue={"memory"} enumValues={{
            "memory": "Stores the sink's buffer in memory. This is more performant, but less durable. Data will be lost if Vector is restarted forcefully.",
            "disk": "Stores the sink's buffer on disk. This is less performant, but durable. Data will not be lost between restarts."
          }} examples={["memory", "disk"]} groups={[]} name={"type"} path={"buffer"} relevantWhen={null} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "type"
            }}>{`type`}</h4>
            <p>{`The buffer's type and storage mechanism.`}</p>
          </Field>
          <Field common={false} defaultValue={"block"} enumValues={{
            "block": "Applies back pressure when the buffer is full. This prevents data loss, but will cause data to pile up on the edge.",
            "drop_newest": "Drops new data as it's received. This data is lost. This should be used when performance is the highest priority."
          }} examples={["block", "drop_newest"]} groups={[]} name={"when_full"} path={"buffer"} relevantWhen={null} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "when_full"
            }}>{`when_full`}</h4>
            <p>{`The behavior when the buffer becomes full.`}</p>
          </Field>
        </Fields>
      </Field>
      <Field common={true} defaultValue={null} enumValues={null} examples={[]} groups={[]} name={"encoding"} path={null} relevantWhen={null} required={true} templateable={false} type={"table"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "encoding"
        }}>{`encoding`}</h3>
        <p>{`Configures the encoding specific sink behavior.`}</p>
        <Fields filters={false} mdxType="Fields">
          <Field common={true} defaultValue={null} enumValues={{
            "json": "Each event is encoded into JSON and the payload is represented as a JSON array.",
            "text": "Each event is encoded into text via the `message` key and the payload is new line delimited."
          }} examples={["json", "text"]} groups={[]} name={"codec"} path={"encoding"} relevantWhen={null} required={true} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "codec"
            }}>{`codec`}</h4>
            <p>{`The encoding codec used to serialize the events before outputting.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[["timestamp", "message", "host"]]} groups={[]} name={"except_fields"} path={"encoding"} relevantWhen={null} required={false} templateable={false} type={"[string]"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "except_fields"
            }}>{`except_fields`}</h4>
            <p>{`Prevent the sink from encoding the specified labels.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[["timestamp", "message", "host"]]} groups={[]} name={"only_fields"} path={"encoding"} relevantWhen={null} required={false} templateable={false} type={"[string]"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "only_fields"
            }}>{`only_fields`}</h4>
            <p>{`Limit the sink to only encoding the specified labels.`}</p>
          </Field>
          <Field common={false} defaultValue={"rfc3339"} enumValues={{
            "rfc3339": "Format as an RFC3339 string",
            "unix": "Format as a unix timestamp, can be parsed as a Clickhouse DateTime"
          }} examples={["rfc3339", "unix"]} groups={[]} name={"timestamp_format"} path={"encoding"} relevantWhen={null} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "timestamp_format"
            }}>{`timestamp_format`}</h4>
            <p>{`How to format event timestamps.`}</p>
          </Field>
        </Fields>
      </Field>
      <Field common={true} defaultValue={null} enumValues={null} examples={["logs.papertrailapp.com:12345"]} groups={[]} name={"endpoint"} path={null} relevantWhen={null} required={true} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "endpoint"
        }}>{`endpoint`}</h3>
        <p>{`The endpoint to stream logs to.
See `}<a parentName="p" {...{
            "href": "#obtaining-a-log-destination"
          }}>{`Obtaining a Log Destination`}</a>{` for more info.`}</p>
      </Field>
      <Field common={true} defaultValue={true} enumValues={null} examples={[true, false]} groups={[]} name={"healthcheck"} path={null} relevantWhen={null} required={false} templateable={false} type={"bool"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "healthcheck"
        }}>{`healthcheck`}</h3>
        <p>{`Enables/disables the sink healthcheck upon start.
See `}<a parentName="p" {...{
            "href": "#health-checks"
          }}>{`Health Checks`}</a>{` for more info.`}</p>
      </Field>
    </Fields>
    <h2 {...{
      "id": "how-it-works"
    }}>{`How It Works`}</h2>
    <h3 {...{
      "id": "buffers"
    }}>{`Buffers`}</h3>
    <SVG src="/img/buffers.svg" mdxType="SVG" />
    <p>{`The `}<inlineCode parentName="p">{`papertrail`}</inlineCode>{` sink buffers events as shown in
the diagram above. This helps to smooth out data processing if the downstream
service applies backpressure. Buffers are controlled via the
`}<a parentName="p" {...{
        "href": "#buffer"
      }}><inlineCode parentName="a">{`buffer.*`}</inlineCode></a>{` options.`}</p>
    <h3 {...{
      "id": "environment-variables"
    }}>{`Environment Variables`}</h3>
    <p>{`Environment variables are supported through all of Vector's configuration.
Simply add `}<inlineCode parentName="p">{`\${MY_ENV_VAR}`}</inlineCode>{` in your Vector configuration file and the variable
will be replaced before being evaluated.`}</p>
    <p>{`You can learn more in the
`}<a parentName="p" {...{
        "href": "/docs/setup/configuration/#environment-variables"
      }}>{`Environment Variables`}</a>{` section.`}</p>
    <h3 {...{
      "id": "health-checks"
    }}>{`Health Checks`}</h3>
    <p>{`Health checks ensure that the downstream service is accessible and ready to
accept data. This check is performed upon sink initialization.
If the health check fails an error will be logged and Vector will proceed to
start.`}</p>
    <h4 {...{
      "id": "require-health-checks"
    }}>{`Require Health Checks`}</h4>
    <p>{`If you'd like to exit immediately upon a health check failure, you can
pass the `}<inlineCode parentName="p">{`--require-healthy`}</inlineCode>{` flag:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`vector --config /etc/vector/vector.toml --require-healthy
`}</code></pre>
    <h4 {...{
      "id": "disable-health-checks"
    }}>{`Disable Health Checks`}</h4>
    <p>{`If you'd like to disable health checks for this sink you can set the
`}<inlineCode parentName="p">{`healthcheck`}</inlineCode>{` option to `}<inlineCode parentName="p">{`false`}</inlineCode>{`.`}</p>
    <h3 {...{
      "id": "obtaining-a-log-destination"
    }}>{`Obtaining a Log Destination`}</h3>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Register for a free account at `}<a parentName="p" {...{
            "href": "https://papertrailapp.com/signup?plan=free"
          }}>{`Papertrailapp.com`}</a></p>
      </li>
      <li parentName="ol">
        <p parentName="li"><a parentName="p" {...{
            "href": "https://papertrailapp.com/destinations/new"
          }}>{`Create a Log Destination`}</a>{` to get a Log Destination
and ensure that TCP is enabled.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Set the log destination as the `}<a parentName="p" {...{
            "href": "#endpoint"
          }}><inlineCode parentName="a">{`endpoint`}</inlineCode></a>{` option and start shipping your logs!`}</p>
      </li>
    </ol>
    <h3 {...{
      "id": "streaming"
    }}>{`Streaming`}</h3>
    <p>{`The `}<inlineCode parentName="p">{`papertrail`}</inlineCode>{` sink streams data on a real-time
event-by-event basis. It does not batch data.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
