const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};
const CodeHeader = makeShortcode("CodeHeader");
const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`The Vector `}<inlineCode parentName="p">{`reduce`}</inlineCode>{` transform
accepts and outputs `}<a parentName="p" {...{
        "href": "/docs/about/data-model/log/"
      }}><inlineCode parentName="a">{`log`}</inlineCode></a>{` events, allowing you to
combine multiple events into a single event based on a set of identifiers.`}</p>
    {
      /*
          THIS FILE IS AUTOGENERATED!
           To make changes please edit the template located at:
           website/docs/reference/transforms/reduce.md.erb
      */
    }
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <Tabs block={true} defaultValue="common" values={[{
      "label": "Common",
      "value": "common"
    }, {
      "label": "Advanced",
      "value": "advanced"
    }]} mdxType="Tabs">
      <TabItem value="common" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[transforms.my_transform_id]
  # General
  type = "reduce" # required
  inputs = ["my-source-or-transform-id"] # required
  identifier_fields = [] # optional, default

  # Ends when
  ends_when.type = "check_fields" # optional, default
  ends_when."message.eq" = "this is the content to match against" # example
  ends_when."message.eq" = ["match this", "or this"] # example
  ends_when."message.contains" = "foo" # example
  ends_when."message.contains" = ["foo", "bar"] # example
  ends_when."environment.ends_with" = "-staging" # example
  ends_when."environment.ends_with" = ["-staging", "-running"] # example
  ends_when."message.regex" = " (any|of|these|five|words) " # example
  ends_when."environment.starts_with" = "staging-" # example
  ends_when."environment.starts_with" = ["staging-", "running-"] # example
`}</code></pre>
      </TabItem>
      <TabItem value="advanced" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[transforms.my_transform_id]
  # General
  type = "reduce" # required
  inputs = ["my-source-or-transform-id"] # required
  expire_after_ms = 30000 # optional, default
  flush_period_ms = 1000 # optional, default
  identifier_fields = [] # optional, default

  # Ends when
  ends_when.type = "check_fields" # optional, default
  ends_when."message.eq" = "this is the content to match against" # example
  ends_when."message.eq" = ["match this", "or this"] # example
  ends_when."host.exists" = true # example
  ends_when."method.neq" = "POST" # example
  ends_when."method.neq" = ["POST", "GET"] # example
  ends_when."message.not_contains" = "some phrase to ignore" # example
  ends_when."unit.not_starts_with" = "sys-" # example
  ends_when."unit.not_ends_with" = ".device" # example
  ends_when."message.contains" = "foo" # example
  ends_when."message.contains" = ["foo", "bar"] # example
  ends_when."environment.ends_with" = "-staging" # example
  ends_when."environment.ends_with" = ["-staging", "-running"] # example
  ends_when."message.ip_cidr_contains" = "10.0.0.0/8" # example
  ends_when."message.ip_cidr_contains" = ["2000::/10", "192.168.0.0/16"] # example
  ends_when."message.regex" = " (any|of|these|five|words) " # example
  ends_when."environment.starts_with" = "staging-" # example
  ends_when."environment.starts_with" = ["staging-", "running-"] # example

  # Merge strategies
  merge_strategies.method = "discard" # example
  merge_strategies.path = "discard" # example
  merge_strategies.duration_ms = "sum" # example
  merge_strategies.query = "array" # example
`}</code></pre>
      </TabItem>
    </Tabs>
    <h2 {...{
      "id": "options"
    }}>{`Options`}</h2>
    <Fields filters={true} mdxType="Fields">
      <Field common={true} defaultValue={null} enumValues={null} examples={[]} groups={[]} name={"ends_when"} path={null} relevantWhen={null} required={false} templateable={false} type={"table"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "ends_when"
        }}>{`ends_when`}</h3>
        <p>{`A condition used to distinguish the final event of a transaction. If this
condition resolves to true for an event the transaction it belongs to is
immediately flushed.`}</p>
        <Fields filters={false} mdxType="Fields">
          <Field common={true} defaultValue={"check_fields"} enumValues={{
            "check_fields": "Allows you to check individual fields against a list of conditions.",
            "is_log": "Returns true if the event is a log.",
            "is_metric": "Returns true if the event is a metric."
          }} examples={["check_fields", "is_log", "is_metric"]} groups={[]} name={"type"} path={"ends_when"} relevantWhen={null} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "type"
            }}>{`type`}</h4>
            <p>{`The type of the condition to execute.`}</p>
          </Field>
          <Field common={true} defaultValue={null} enumValues={null} examples={[{
            "message.eq": "this is the content to match against"
          }, {
            "message.eq": ["match this", "or this"]
          }]} groups={[]} name={"`[field-name]`.eq"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field-nameeq"
            }}><inlineCode parentName="h4">{`[field-name]`}</inlineCode>{`.eq`}</h4>
            <p>{`Check whether a fields contents exactly matches the value specified. This may
be a single string or a list of strings, in which case this evaluates to true
if any of the list matches.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[{
            "host.exists": true
          }]} groups={[]} name={"`[field-name]`.exists"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"bool"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field-nameexists"
            }}><inlineCode parentName="h4">{`[field-name]`}</inlineCode>{`.exists`}</h4>
            <p>{`Check whether a field exists or does not exist, depending on the provided value
being `}<inlineCode parentName="p">{`true`}</inlineCode>{` or `}<inlineCode parentName="p">{`false`}</inlineCode>{` respectively.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[{
            "method.neq": "POST"
          }, {
            "method.neq": ["POST", "GET"]
          }]} groups={[]} name={"`[field-name]`.neq"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field-nameneq"
            }}><inlineCode parentName="h4">{`[field-name]`}</inlineCode>{`.neq`}</h4>
            <p>{`Check whether a fields contents does not match the value specified. This may be
a single string or a list of strings, in which case this evaluates to false if
any of the list matches.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[{
            "message.not_contains": "some phrase to ignore"
          }, {
            "unit.not_starts_with": "sys-"
          }, {
            "unit.not_ends_with": ".device"
          }]} groups={[]} name={"`[field-name]`.not_`[condition]`"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"any"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field-namenot_condition"
            }}><inlineCode parentName="h4">{`[field-name]`}</inlineCode>{`.not_`}<inlineCode parentName="h4">{`[condition]`}</inlineCode></h4>
            <p>{`Check if the given `}<inlineCode parentName="p">{`[condition]`}</inlineCode>{` does not match.`}</p>
          </Field>
          <Field common={true} defaultValue={null} enumValues={null} examples={[{
            "message.contains": "foo"
          }, {
            "message.contains": ["foo", "bar"]
          }]} groups={[]} name={"`[field_name]`.contains"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field_namecontains"
            }}><inlineCode parentName="h4">{`[field_name]`}</inlineCode>{`.contains`}</h4>
            <p>{`Checks whether a string field contains a string argument. This may be a single
string or a list of strings, in which case this evaluates to true if any of the
list matches.`}</p>
          </Field>
          <Field common={true} defaultValue={null} enumValues={null} examples={[{
            "environment.ends_with": "-staging"
          }, {
            "environment.ends_with": ["-staging", "-running"]
          }]} groups={[]} name={"`[field_name]`.ends_with"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field_nameends_with"
            }}><inlineCode parentName="h4">{`[field_name]`}</inlineCode>{`.ends_with`}</h4>
            <p>{`Checks whether a string field ends with a string argument. This may be a single
string or a list of strings, in which case this evaluates to true if any of the
list matches.`}</p>
          </Field>
          <Field common={false} defaultValue={null} enumValues={null} examples={[{
            "message.ip_cidr_contains": "10.0.0.0/8"
          }, {
            "message.ip_cidr_contains": ["2000::/10", "192.168.0.0/16"]
          }]} groups={[]} name={"`[field_name]`.ip_cidr_contains"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field_nameip_cidr_contains"
            }}><inlineCode parentName="h4">{`[field_name]`}</inlineCode>{`.ip_cidr_contains`}</h4>
            <p>{`Checks whether an IP field is contained within a given `}<a parentName="p" {...{
                "href": "https://en.wikipedia.org/wiki/Classless_Inter-Domain_Routing"
              }}>{`IP CIDR`}</a>{`
(works with IPv4 and IPv6). This may be a single string or a list of strings,
in which case this evaluates to true if the IP field is contained within any of
the CIDRs in the list.`}</p>
          </Field>
          <Field common={true} defaultValue={null} enumValues={null} examples={[{
            "message.regex": " (any|of|these|five|words) "
          }]} groups={[]} name={"`[field_name]`.regex"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field_nameregex"
            }}><inlineCode parentName="h4">{`[field_name]`}</inlineCode>{`.regex`}</h4>
            <p>{`Checks whether a string field matches a `}<a parentName="p" {...{
                "href": "https://en.wikipedia.org/wiki/Regular_expression"
              }}>{`regular expression`}</a>{`.
Vector uses the `}<a parentName="p" {...{
                "href": "https://docs.rs/regex/1.3.6/regex/#syntax"
              }}>{`documented Rust Regex syntax`}</a>{`. Note
that this condition is considerably more expensive than a regular string match
(such as `}<inlineCode parentName="p">{`starts_with`}</inlineCode>{` or `}<inlineCode parentName="p">{`contains`}</inlineCode>{`) so the use of those conditions are
preferred where possible.`}</p>
          </Field>
          <Field common={true} defaultValue={null} enumValues={null} examples={[{
            "environment.starts_with": "staging-"
          }, {
            "environment.starts_with": ["staging-", "running-"]
          }]} groups={[]} name={"`[field_name]`.starts_with"} path={"ends_when"} relevantWhen={{
            "type": "check_fields"
          }} required={false} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field_namestarts_with"
            }}><inlineCode parentName="h4">{`[field_name]`}</inlineCode>{`.starts_with`}</h4>
            <p>{`Checks whether a string field starts with a string argument. This may be a
single string or a list of strings, in which case this evaluates to true if any
of the list matches.`}</p>
          </Field>
        </Fields>
      </Field>
      <Field common={false} defaultValue={30000} enumValues={null} examples={[30000]} groups={[]} name={"expire_after_ms"} path={null} relevantWhen={null} required={false} templateable={false} type={"int"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "expire_after_ms"
        }}>{`expire_after_ms`}</h3>
        <p>{`A maximum period of time to wait after the last event is received before a
combined event should be considered complete.`}</p>
      </Field>
      <Field common={false} defaultValue={1000} enumValues={null} examples={[1000]} groups={[]} name={"flush_period_ms"} path={null} relevantWhen={null} required={false} templateable={false} type={"int"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "flush_period_ms"
        }}>{`flush_period_ms`}</h3>
        <p>{`Controls the frequency that Vector checks for (and flushes) expired events.`}</p>
      </Field>
      <Field common={true} defaultValue={[]} enumValues={null} examples={[["request_id"], ["user_id", "transaction_id"]]} groups={[]} name={"identifier_fields"} path={null} relevantWhen={null} required={false} templateable={false} type={"[string]"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "identifier_fields"
        }}>{`identifier_fields`}</h3>
        <p>{`An ordered list of fields by which to group events. Each group is combined
independently, allowing you to keep independent events separate. When no fields
are specified, all events will be combined in a single group. Events missing a
specified field will be combined in their own group.`}</p>
      </Field>
      <Field common={false} defaultValue={null} enumValues={null} examples={[]} groups={[]} name={"merge_strategies"} path={null} relevantWhen={null} required={false} templateable={false} type={"table"} unit={null} warnings={[]} mdxType="Field">
        <h3 {...{
          "id": "merge_strategies"
        }}>{`merge_strategies`}</h3>
        <p>{`A map of field names to custom merge strategies. For each field specified this
strategy will be used for combining events rather than the default behavior.`}</p>
        <p>{`The default behavior is as follows:`}</p>
        <ol>
          <li parentName="ol">{`The first value of a string field is kept, subsequent values are discarded.`}</li>
          <li parentName="ol">{`For timestamp fields the first is kept and a new field `}<inlineCode parentName="li">{`[field-name]_end`}</inlineCode>{` is
added with the last received timestamp value.`}</li>
          <li parentName="ol">{`Numeric values are summed.`}</li>
        </ol>
        <Fields filters={false} mdxType="Fields">
          <Field common={true} defaultValue={null} enumValues={{
            "array": "Each value is appended to an array.",
            "concat": "Concatenate each string value (delimited with a space).",
            "discard": "Discard all but the first value found.",
            "sum": "Sum all numeric values.",
            "max": "The maximum of all numeric values.",
            "min": "The minimum of all numeric values."
          }} examples={[{
            "method": "discard"
          }, {
            "path": "discard"
          }, {
            "duration_ms": "sum"
          }, {
            "query": "array"
          }]} groups={[]} name={"`[field-name]`"} path={"merge_strategies"} relevantWhen={null} required={true} templateable={false} type={"string"} unit={null} warnings={[]} mdxType="Field">
            <h4 {...{
              "id": "field-name"
            }}><inlineCode parentName="h4">{`[field-name]`}</inlineCode></h4>
            <p>{`The custom merge strategy to use for a field.`}</p>
          </Field>
        </Fields>
      </Field>
    </Fields>
    <h2 {...{
      "id": "examples"
    }}>{`Examples`}</h2>
    <Tabs block={true} defaultValue="default" select={false} values={[{
      "label": "Default",
      "value": "default"
    }, {
      "label": "With Identifier Fields",
      "value": "with-identifier-fields"
    }]} mdxType="Tabs">
      <TabItem value="default" mdxType="TabItem">
        <p>{`Given the following configuration:`}</p>
        <CodeHeader fileName="vector.toml" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-toml"
          }}>{`[transforms.transaction_events]
  type = "reduce"
  inputs = [...]
`}</code></pre>
        <p>{`And these three `}<a parentName="p" {...{
            "href": "/docs/about/data-model/log/"
          }}><inlineCode parentName="a">{`log`}</inlineCode>{` events`}</a>{`:`}</p>
        <CodeHeader fileName="first log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "A thing is happening",
  "custom_string_field_1": "value1",
  "this_much": 1
}
`}</code></pre>
        <p>{`Followed by:`}</p>
        <CodeHeader fileName="second log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "That thing is still happening",
  "custom_string_field_2": "value2",
  "this_much": 3
}
`}</code></pre>
        <p>{`And, finally:`}</p>
        <CodeHeader fileName="third log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "That thing is concluded",
  "custom_string_field_3": "value3",
  "this_much": 2
}
`}</code></pre>
        <p>{`A single log event will eventually be produced:`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "A thing is happening",
  "custom_string_field_1": "value1",
  "custom_string_field_2": "value2",
  "custom_string_field_3": "value3",
  "this_much": 6
}
`}</code></pre>
        <p>{`Notice that string values have not been overridden and integer values have been summed.`}</p>
      </TabItem>
      <TabItem value="with-identifier-fields" mdxType="TabItem">
        <p>{`Given the following configuration:`}</p>
        <CodeHeader fileName="vector.toml" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-toml"
          }}>{`[transforms.transaction_events]
  type = "reduce"
  inputs = [...]

  identifier_fields = ["request_id"]

  [transforms.transaction_events.merge_strategies]
    request_action = "array"

  [transforms.transaction_events.ends_when]
    "request_concluded.exists" = true
`}</code></pre>
        <p>{`And these three `}<a parentName="p" {...{
            "href": "/docs/about/data-model/log/"
          }}><inlineCode parentName="a">{`log`}</inlineCode>{` events`}</a>{`:`}</p>
        <CodeHeader fileName="first log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "A thing is happening",
  "request_id": "1",
  "request_action": "start",
  "this_much": 1
}
`}</code></pre>
        <p>{`Followed by:`}</p>
        <CodeHeader fileName="second log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "That thing is still happening",
  "request_id": "1",
  "request_action": "bounced",
  "this_much": 2
}
`}</code></pre>
        <p>{`And:`}</p>
        <CodeHeader fileName="third log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "A different thing is happening",
  "request_id": "2",
  "request_action": "start",
  "this_much": 5
}
`}</code></pre>
        <p>{`And, finally:`}</p>
        <CodeHeader fileName="fourth log event" mdxType="CodeHeader" />
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "That thing concluded",
  "request_id": "1",
  "request_action": "rebound",
  "request_concluded": true,
  "this_much": 3
}
`}</code></pre>
        <p>{`A single log event will be produced and flushed immediately:`}</p>
        <pre><code parentName="pre" {...{
            "className": "language-javascript"
          }}>{`{
  "message": "A thing is happening",
  "request_id": "1",
  "request_action": ["start","bounced","rebound"]
  "request_concluded": true,
  "this_much": 6
}
`}</code></pre>
