  /* @jsx mdx */

export const rightToc = [{
  value: 'Upgrade Guide',
  id: 'upgrade-guide',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`To optimize throughput we originally defaulted the `}<inlineCode parentName="p">{`elasticsearch`}</inlineCode>{` sink
`}<inlineCode parentName="p">{`compression`}</inlineCode>{` option to `}<inlineCode parentName="p">{`gzip`}</inlineCode>{`. It is our philosohpy that Vector's defaults
should optimize performance and throughput, but not at the expense of causing
errors. Unfortunately, AWS hosted Elasticsearch does not support compression,
and therefore we've made this feature opt-in.`}</p>
    <h3 {...{
      "id": "upgrade-guide"
    }}>{`Upgrade Guide`}</h3>
    <p>{`Upgrading is easy. Add the following if you want to enabled Gzip compression:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-diff",
        "metastring": "title=\"vector.toml\"",
        "title": "\"vector.toml\""
      }}>{` [sinks.es]
   type = "elasticsearch"
+  compression = "gzip"
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
