export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`Amazon Linux is an operating system generally used on AWS. This document
will cover how to install Vector on this operating system.`}</p>
    {
      /*
          THIS FILE IS AUTOGENERATED!
           To make changes please edit the template located at:
           website/docs/setup/installation/operating-systems/amazon-linux.md.erb
      */
    }
    <h2 {...{
      "id": "install"
    }}>{`Install`}</h2>
    <Tabs block={true} defaultValue="daemon" values={[{
      "label": "As a Daemon",
      "value": "daemon"
    }]} mdxType="Tabs">
      <TabItem value="daemon" mdxType="TabItem">
        <p>{`The `}<a parentName="p" {...{
            "href": "/docs/setup/deployment/strategies/#daemon"
          }}>{`daemon deployment strategy`}</a>{` is designed for data
collection on a single host. Vector runs in the background, in its own process,
collecting `}<em parentName="p">{`all`}</em>{` data for that host.
Typically data is collected from a process manager, such as Journald via
Vector's `}<a parentName="p" {...{
            "href": "/docs/reference/sources/journald/"
          }}><inlineCode parentName="a">{`journald`}</inlineCode>{` source`}</a>{`, but can be collected
through any of Vector's `}<a parentName="p" {...{
            "href": "/docs/reference/sources/"
          }}>{`sources`}</a>{`.
The following diagram demonstrates how it works.`}</p>
        <DaemonDiagram platformName={null} sourceName={null} sinkName={null} mdxType="DaemonDiagram" />
        <hr></hr>
        <Tabs centered={true} className={"rounded"} defaultValue={"rpm"} placeholder="Please choose an installation method..." select={false} size={null} values={[{
          "group": "Package managers",
          "label": "RPM",
          "value": "rpm"
        }, {
          "group": "Nones",
          "label": "Vector CLI",
          "value": "vector-cli"
        }, {
          "group": "Platforms",
          "label": "Docker CLI",
          "value": "docker-cli"
        }, {
          "group": "Platforms",
          "label": "Docker Compose",
          "value": "docker-compose"
        }]} mdxType="Tabs">
          <TabItem value="rpm" mdxType="TabItem">
            <Steps headingDepth={3} mdxType="Steps">
              <Tabs centered={true} className="rounded" defaultValue="arm64" values={[{
                "label": "ARM64",
                "value": "arm64"
              }, {
                "label": "ARMv7",
                "value": "armv7"
              }, {
                "label": "x86_64",
                "value": "x86_64"
              }]} mdxType="Tabs">
                <TabItem value="arm64" mdxType="TabItem">
                  <ol>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "download-the-vector-rpm-file"
                      }}>{`Download the Vector `}<inlineCode parentName="h3">{`.rpm`}</inlineCode>{` file`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`curl -O https://packages.timber.io/vector/0.9.X/vector-aarch64.rpm
`}</code></pre>
                      <p parentName="li"><a parentName="p" {...{
                          "href": "/docs/setup/installation/package-managers/rpm/#versions"
                        }}>{`Looking for a specific version?`}</a></p>
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "install-the-vector-rpm-package-directly"
                      }}>{`Install the Vector `}<inlineCode parentName="h3">{`.rpm`}</inlineCode>{` package directly`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`sudo rpm -i vector-aarch64.rpm
`}</code></pre>
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "configure-vector"
                      }}>{`Configure Vector`}</h3>
                      <ConfigExample format="toml" path={"/etc/vector/vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "start-vector"
                      }}>{`Start Vector`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`sudo systemctl start vector
`}</code></pre>
                    </li>
                  </ol>
                </TabItem>
                <TabItem value="armv7" mdxType="TabItem">
                  <ol>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "download-the-vector-rpm-file-1"
                      }}>{`Download the Vector `}<inlineCode parentName="h3">{`.rpm`}</inlineCode>{` file`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`curl -O https://packages.timber.io/vector/0.9.X/vector-armv7hl.rpm
`}</code></pre>
                      <p parentName="li"><a parentName="p" {...{
                          "href": "/docs/setup/installation/package-managers/rpm/#versions"
                        }}>{`Looking for a specific version?`}</a></p>
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "install-the-vector-rpm-package-directly-1"
                      }}>{`Install the Vector `}<inlineCode parentName="h3">{`.rpm`}</inlineCode>{` package directly`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`sudo rpm -i vector-armv7hl.rpm
`}</code></pre>
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "configure-vector-1"
                      }}>{`Configure Vector`}</h3>
                      <ConfigExample format="toml" path={"/etc/vector/vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "start-vector-1"
                      }}>{`Start Vector`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`sudo systemctl start vector
`}</code></pre>
                    </li>
                  </ol>
                </TabItem>
                <TabItem value="x86_64" mdxType="TabItem">
                  <ol>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "download-the-vector-rpm-file-2"
                      }}>{`Download the Vector `}<inlineCode parentName="h3">{`.rpm`}</inlineCode>{` file`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`curl -O https://packages.timber.io/vector/0.9.X/vector-x86_64.rpm
`}</code></pre>
                      <p parentName="li"><a parentName="p" {...{
                          "href": "/docs/setup/installation/package-managers/rpm/#versions"
                        }}>{`Looking for a specific version?`}</a></p>
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "install-the-vector-rpm-package-directly-2"
                      }}>{`Install the Vector `}<inlineCode parentName="h3">{`.rpm`}</inlineCode>{` package directly`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`sudo rpm -i vector-x86_64.rpm
`}</code></pre>
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "configure-vector-2"
                      }}>{`Configure Vector`}</h3>
                      <ConfigExample format="toml" path={"/etc/vector/vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                    </li>
                    <li parentName="ol">
                      <h3 parentName="li" {...{
                        "id": "start-vector-2"
                      }}>{`Start Vector`}</h3>
                      <pre parentName="li"><code parentName="pre" {...{
                          "className": "language-bash"
                        }}>{`sudo systemctl start vector
`}</code></pre>
                    </li>
                  </ol>
                </TabItem>
              </Tabs>
            </Steps>
          </TabItem>
          <TabItem value="vector-cli" mdxType="TabItem">
            <Steps headingDepth={3} mdxType="Steps">
              <ol>
                <li>
                  <h3 {...{
                    "id": "install-vector"
                  }}>{`Install Vector`}</h3>
                  <InstallationCommand mdxType="InstallationCommand" />
                  <p>{`Or choose your `}<a parentName="p" {...{
                      "href": "/docs/setup/installation/"
                    }}>{`preferred method`}</a>{`.`}</p>
                </li>
                <li>
                  <h3 {...{
                    "id": "configure-vector-3"
                  }}>{`Configure Vector`}</h3>
                  <ConfigExample format="toml" path={"vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                </li>
                <li>
                  <h3 {...{
                    "id": "start-vector-3"
                  }}>{`Start Vector`}</h3>
                  <pre><code parentName="pre" {...{
                      "className": "language-bash"
                    }}>{`vector --config vector.toml
`}</code></pre>
                  <p>{`That's it! Simple and to the point. Hit `}<inlineCode parentName="p">{`ctrl+c`}</inlineCode>{` to exit.`}</p>
                </li>
              </ol>
            </Steps>
          </TabItem>
          <TabItem value="docker-cli" mdxType="TabItem">
            <Steps headingDepth={3} mdxType="Steps">
              <ol>
                <li>
                  <h3 {...{
                    "id": "configure-vector-4"
                  }}>{`Configure Vector`}</h3>
                  <ConfigExample format="toml" path={"/etc/vector/vector.toml"} sourceName={"journald"} sinkName={null} mdxType="ConfigExample" />
                </li>
                <li>
                  <h3 {...{
                    "id": "start-the-vector-container"
                  }}>{`Start the Vector container`}</h3>
                  <pre><code parentName="pre" {...{
                      "className": "language-bash"
                    }}>{`docker run \\
  -v $PWD/vector.toml:/etc/vector/vector.toml:ro \\
  timberio/vector:latest-alpine
`}</code></pre>
                  <CodeExplanation mdxType="CodeExplanation">
                    <ul>
                      <li parentName="ul">{`The `}<inlineCode parentName="li">{`-v $PWD/vector.to...`}</inlineCode>{` flag passes your custom configuration to Vector.`}</li>
                      <li parentName="ul">{`The `}<inlineCode parentName="li">{`timberio/vector:latest-alpine`}</inlineCode>{` is the default image we've chosen, you are welcome to use `}<a parentName="li" {...{
                          "href": "/docs/setup/installation/platforms/docker/#variants"
                        }}>{`other image variants`}</a>{`.`}</li>
                    </ul>
                  </CodeExplanation>
                  <p>{`That's it! Simple and to the point. Hit `}<inlineCode parentName="p">{`ctrl+c`}</inlineCode>{` to exit.`}</p>
                </li>
              </ol>
            </Steps>
          </TabItem>
          <TabItem value="docker-compose" mdxType="TabItem">
            <p>{`compose!`}</p>
          </TabItem>
        </Tabs>
      </TabItem>
    </Tabs>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
