};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Upgrade Guide',
  id: 'upgrade-guide',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`GCP Cloud Storage buckets with `}<a parentName="p" {...{
        "href": "https://cloud.google.com/storage/docs/uniform-bucket-level-access"
      }}>{`uniform bucket-level access`}</a>{`
don't support setting ACL for objects inside them (HTTP 400 error is returned).
Therefore, the Vector `}<inlineCode parentName="p">{`gcp_cloud_storage`}</inlineCode>{` sink no longer supplies a
`}<inlineCode parentName="p">{`x-goog-acl`}</inlineCode>{` header by default.`}</p>
    <h2 {...{
      "id": "upgrade-guide"
    }}>{`Upgrade Guide`}</h2>
    <p>{`If you wish to set an ACL for your GCP object you'll need to explicitly set
the `}<inlineCode parentName="p">{`acl`}</inlineCode>{` option:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-diff",
        "metastring": "title=\"vector.toml\"",
        "title": "\"vector.toml\""
      }}>{` [sinks.gcp_cloud_storage]
   type = "gcp_cloud_storage"
+  acl = "projectPrivate" # change as desired
`}</code></pre>
    <p>{`That's it!`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
