const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The `}<inlineCode parentName="p">{`endpoint`}</inlineCode>{` and `}<inlineCode parentName="p">{`region`}</inlineCode>{` options have been dropped in the `}<a parentName="p" {...{
        "href": "/docs/reference/sinks/elasticsearch/"
      }}><inlineCode parentName="a">{`elasticsearch`}</inlineCode>{`
sink`}</a>{` in favor of using the `}<inlineCode parentName="p">{`host`}</inlineCode>{` option.`}</p>
    <h2 {...{
      "id": "upgrade-guide"
    }}>{`Upgrade Guide`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-diff",
        "metastring": "title=\"vector.toml\"",
        "title": "\"vector.toml\""
      }}>{` [sinks.es]
   type = "elasticsearch"
-  endpoint = "http://my-domain.us-east-1.es.amazonaws.com"
-  region = "us-east-1"
+  host = "http://my-domain.us-east-1.es.amazonaws.com"
`}</code></pre>
    <p>{`You can find your AWS ES domain in the AWS console. Simply provide the full
domain URL as the `}<inlineCode parentName="p">{`host`}</inlineCode>{` value.`}</p>
    </MDXLayout>;
}

;
