  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`The Vector `}<inlineCode parentName="p">{`stdin`}</inlineCode>{` source
ingests data through `}<a parentName="p" {...{
        "href": "https://en.wikipedia.org/wiki/Standard_streams#Standard_input_(stdin)"
      }}>{`standard input (STDIN)`}</a>{` and outputs
`}<a parentName="p" {...{
        "href": "/docs/about/data-model/log/"
      }}><inlineCode parentName="a">{`log`}</inlineCode></a>{` events.`}</p>
    {
      /*
          THIS FILE IS AUTOGENERATED!
           To make changes please edit the template located at:
           website/docs/reference/sources/stdin.md.erb
      */
    }
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <Tabs block={true} defaultValue="common" values={[{
      "label": "Common",
      "value": "common"
    }, {
      "label": "Advanced",
      "value": "advanced"
    }]} mdxType="Tabs">
      <TabItem value="common" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[sources.my_source_id]
  type = "stdin" # required
`}</code></pre>
      </TabItem>
      <TabItem value="advanced" mdxType="TabItem">
        <pre><code parentName="pre" {...{
            "className": "language-toml",
            "metastring": "title=\"vector.toml\"",
            "title": "\"vector.toml\""
          }}>{`[sources.my_source_id]
  # General
  type = "stdin" # required
  max_length = 102400 # optional, default, bytes

  # Context
  host_key = "host" # optional, default
`}</code></pre>
      </TabItem>
    </Tabs>
